`).join(&#39;&#39;)}`;};const customHits = instantsearch.connectors.connectHits( renderHits );search.addWidgets([instantsearch.widgets.searchBox({container: &#39;#searchbox&#39;,showReset: false,}),instantsearch.widgets.stats({container: &#39;#stats&#39;,}),customHits({container: document.querySelector(&#39;#hits&#39;),}),instantsearch.widgets.pagination({container: &#39;#pagination&#39;,maxPages: 20,})]);search.start();
